mapper.select:
SET GLOBAL group_concat_max_len=102400; SET SESSION group_concat_max_len=102400;
SELECT o.open_id AS openId, ifnull(w.nickname, '匿名') AS nickName, o.order_id AS orderId, o.STATUS AS orderStatus, o.pay_amount AS payAmount, o.expired_time as expiredTime,
concat('<products>', GROUP_CONCAT( '<product>', concat( '<productId>' , op.product_id , '</productId>' ), concat( '<productSpecId>' , op.product_spec_id , '</productSpecId>' ), concat( '<productName>' , p.product_name , '</productName>' ), concat( '<specName>' , ps.spec_name , '</specName>' ), concat( '<salePrice>' , ps.sale_price , '</salePrice>' ), concat( '<number>' , op.number , '</number>' ), concat( '<imgPath>' , pi.img_path , '</imgPath>' ), '</product>' SEPARATOR '' ),'</products>' ) as xml FROM _order as o INNER JOIN _order_product AS op on o.order_id = op.order_id INNER JOIN _product_spec AS ps on op.product_spec_id = ps.product_spec_id INNER JOIN _product AS p on op.product_id = p.product_id LEFT JOIN _product_img AS pi on (pi.product_id = op.product_id AND pi.type_id = 0 ) LEFT JOIN _wechat AS w ON o.open_id = w.open_id WHERE o.open_id = #{openId} and (#{orderFlag} = 0 or ( #{orderFlag} = 1 and o.status in (0,20) ) ) AND o.delete_time IS NULL GROUP BY o.order_id ORDER BY o. STATUS ASC, o.create_time DESC
